import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import {
  CAlert,
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { SignInWithEmail } from "../../modules/auth/actions";
import classnames from "classnames";

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email : "",
      password: "",
      errors: {}
    }
  }

  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isLogin) {
      this.props.history.push("./dashboard");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isLogin) {
      this.props.history.push("./dashboard"); // push user to dashboard when they login
    }
    if (nextProps.authErrors.errors) {
      this.setState({
        errors: nextProps.authErrors.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  handleLogin = e => {
    e.preventDefault();
    const {email, password} = this.state;
    this.props.SignInWithEmail({email, password});
  };

  render() {
    const { errors } = this.state;
    return (
      <div className="c-app c-default-layout flex-row align-items-center login-container">
        <CContainer>
            <CRow className="justify-content-center">
              <CCol md="4">
                  <div className="login-container">

                  </div>
              </CCol>
            </CRow>
        </CContainer>
      </div>
    )

  }
}

Login.propTypes = {
  SignInWithEmail: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  authErrors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  authErrors: state.authErrors
});
export default connect(
  mapStateToProps,
  { SignInWithEmail }
)(Login);
